import React, {
    Component
} from 'react';
import {
    Card,
    CardImg,
    CardImgOverlay,
    CardTitle
} from 'reactstrap';

import CampsiteInfo from './CampsiteInfoComponent';

class Directory extends Component {
    state = {
        selectedCampsite: null
    };
    directory = this.props.campsites.map(campsite => ( <
        div key = {
            campsite.id
        }
        className = "col-md-5 m-1" >
        <
        Card onClick = {
            () => this.onCampsiteSelect(campsite)
        } >
        <
        CardImg width = "100%"
        src = {
            campsite.image
        }
        alt = {
            campsite.name
        }
        /> <
        CardImgOverlay >
        <
        CardTitle > {
            campsite.name
        } < /CardTitle> <
        /CardImgOverlay> <
        /Card> <
        /div>
    ));

    constructor(props) {
        super(props);
    }

    onCampsiteSelect(campsite) {
        this.setState({
            selectedCampsite: campsite
        });
    }


    render() {
        return ( <
            div className = "container" >
            <
            div className = "row" > {
                this.directory
            } <
            /div> <
            div className = "row" >
            <
            CampsiteInfo campsite = {
                this.state.selectedCampsite
            }
            /> <
            /div> <
            /div>
        );
    }
}


export default Directory;
